                        return new IntPtr((int)HitTestMessageResults.HTCLIENT);
                    }
                    else
                    {
                        handled = true;
                        return new IntPtr((int)HitTestMessageResults.HTTRANSPARENT);
                    }
            }

            handled = false;
            return IntPtr.Zero;
        }

